    @Test
    public void test_list() {
        DataSet ds = new DataSet();
        ds.append();
        ds.setField("A", "A01");
        ds.setField("B", "B01");
        ds.append();
        ds.setField("A", "A02");
        ds.setField("B", "B02");
        ds.append();
        ds.setField("A", "A03");
        ds.setField("B", "B03");

        int i = 0;
        for (@SuppressWarnings("unused")
        Record record : ds) {
            i++;
        }
        assertEquals(i, ds.size());
    }

    @Test
    public void test_delete_a() {
        DataSet ds = new DataSet();
        ds.append().setField("code", "a");
        ds.append().setField("code", "b");
        ds.append().setField("code", "c");

        int i = 0;
        ds.first();
        while (!ds.eof()) {
            i++;
            if (ds.getString("code").equals("a"))
                ds.delete();
            else
                ds.next();
        }
        assertEquals(i, 3);
    }

